tables.table_schema:
CREATE TABLE LoyaltyMembers (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50),
    loyalty_points INT,
    membership_level VARCHAR(20)
);

CREATE TABLE BenefitsAndDiscounts (
    benefit_id INT PRIMARY KEY,
    benefit_description TEXT,
    discount_amount DECIMAL(10,2),
    eligibility_criteria VARCHAR(100)
);

CREATE TABLE loyalty_programs (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(50),
    benefits TEXT,
    discount_rate DECIMAL(5,2)
);

CREATE TABLE member_points (
    member_id INT,
    total_points INT,
    earned_date DATE,
    FOREIGN KEY (member_id) REFERENCES members(member_id)
);

CREATE TABLE MemberBenefits (
    benefit_id INT PRIMARY KEY,
    benefit_name VARCHAR(50) NOT NULL,
    description VARCHAR(255),
    benefit_discount DECIMAL(5, 2) DEFAULT 0
);

CREATE TABLE MemberLevel (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(50) NOT NULL,
    level_threshold INT NOT NULL
);
