tables.table_schema:
CREATE TABLE EmployeeSchedule (
  employee_id INT,
  schedule_date DATE,
  start_time TIME,
  end_time TIME
);

CREATE TABLE Certification (
  certification_id INT,
  certification_name VARCHAR(50),
  expiration_date DATE,
  employee_id INT
);

CREATE TABLE Certifications (
    certification_id SERIAL PRIMARY KEY,
    guide_id UUID REFERENCES AdventureTourGuides(guide_id),
    certification_type VARCHAR(50),
    expiration_date DATE
);

CREATE TABLE AdventureTourGuides (
  guide_id INT PRIMARY KEY,
  guide_name VARCHAR(50) NOT NULL,
  guide_experience INT,
  guide_certification BOOLEAN
);

CREATE TABLE Certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(50) NOT NULL,
  certification_description TEXT
);

CREATE TABLE certifications (
    id INT PRIMARY KEY,
    certification_name VARCHAR(50),
    expiration_date DATE
);

CREATE TABLE tours (
    id INT PRIMARY KEY,
    tour_name VARCHAR(50),
    description TEXT,
    difficulty_level VARCHAR(20)
);

CREATE TABLE tour_guide_assignments (
    id INT PRIMARY KEY,
    tour_id INT,
    tour_guide_id INT
);
