tables.table_schema:
CREATE TABLE customer_feedback (
    id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    feedback_text TEXT,
    rating INT
);

CREATE TABLE CustomerFeedback (
    customer_id INT,
    feedback TEXT,
    rating INT
);

CREATE TABLE TravelServices (
    service_id INT,
    service_name TEXT,
    description TEXT,
    rating INT
);
