tables.table_schema:
CREATE TABLE travel_destinations (
    destination_id INT PRIMARY KEY,
    destination_name VARCHAR,
    avg_expense DECIMAL,
    region VARCHAR
);
CREATE TABLE travel_experiences (
    experience_id INT PRIMARY KEY,
    experience_name VARCHAR,
    destination_id INT,
    price DECIMAL,
    rating DECIMAL
);

CREATE TABLE Locations (
   location_id INT PRIMARY KEY,
   location_name TEXT NOT NULL,
   region TEXT NOT NULL,
   average_rating FLOAT
);

CREATE TABLE luxury_travel_destinations (
    destination_id INT PRIMARY KEY,
    destination_name VARCHAR(255),
    average_trip_cost DECIMAL(10, 2),
    trend_rating INT
);

CREATE TABLE travel_trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(255),
    trend_description TEXT,
    trend_rating INT
);
