tables.table_schema:
CREATE TABLE wildlife_species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50),
  species_category VARCHAR(50)
);

CREATE TABLE tour_guides (
  guide_id INT PRIMARY KEY,
  guide_name VARCHAR(50),
  guide_email VARCHAR(50),
  guide_phone VARCHAR(15)
);

CREATE TABLE sightings (
    id INT PRIMARY KEY,
    species VARCHAR(50),
    location VARCHAR(100),
    sighted_date DATE
);

CREATE TABLE reports (
    report_id INT PRIMARY KEY,
    report_title VARCHAR(100),
    report_content TEXT,
    report_date DATE
);
