tables.table_schema:
CREATE TABLE SustainabilityPractices (
    practice_id INT PRIMARY KEY,
    practice_name VARCHAR(50),
    goal_description TEXT,
    implementation_date DATE
);

CREATE TABLE tour_guides (
    guide_id INT PRIMARY KEY,
    guide_name VARCHAR(50),
    experience_level VARCHAR(20),
    contact_number VARCHAR(15)
);

CREATE TABLE sustainability_measures (
    measure_id INT PRIMARY KEY,
    measure_name VARCHAR(50),
    description TEXT,
    importance_rating INT
);

CREATE TABLE sustainability_reports (
  report_id UUID PRIMARY KEY,
  report_date TIMESTAMP NOT NULL,
  report_content TEXT NOT NULL,
  activity_id UUID REFERENCES ecotourism_activities(activity_id)
);
