tables.table_schema:
CREATE TABLE ecotourism_activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(50),
    activity_description TEXT,
    activity_image_url VARCHAR(255)
);

CREATE TABLE ecotourism_images (
    image_id INT PRIMARY KEY,
    image_url VARCHAR(255),
    activity_id INT,
    FOREIGN KEY (activity_id) REFERENCES ecotourism_activities(activity_id)
);

CREATE TABLE ecotourism_activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(100) UNIQUE,
    activity_description TEXT
);

CREATE TABLE ecotourism_activities (
    activity_id SERIAL PRIMARY KEY,
    activity_name VARCHAR(255) NOT NULL,
    activity_description TEXT,
    activity_location VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    full_name VARCHAR(100) NOT NULL
);

CREATE TABLE images (
    image_id SERIAL PRIMARY KEY,
    image_url TEXT NOT NULL,
    activity_id INT NOT NULL,
    uploaded_at TIMESTAMP
);

CREATE TABLE activities (
    activity_id SERIAL PRIMARY KEY,
    activity_name TEXT NOT NULL,
    description TEXT,
    location TEXT
);

CREATE TABLE ecotourists (
    ecotourist_id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    email TEXT UNIQUE
);

CREATE TABLE bookings (
    booking_id SERIAL PRIMARY KEY,
    ecotourist_id INT NOT NULL,
    activity_id INT NOT NULL,
    booking_date DATE
);

CREATE TABLE activity_categories (
    category_id SERIAL PRIMARY KEY,
    category_name TEXT NOT NULL
);
