tables.table_schema:
CREATE TABLE ecotourist (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    country VARCHAR(50),
    email VARCHAR(50)
);

CREATE TABLE ecotourism_initiative (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    location VARCHAR(50),
    description TEXT
);

CREATE TABLE feedback (
  feedback_id INT PRIMARY KEY,
  tourist_id INT,
  review TEXT,
  rating INT
);

CREATE TABLE tourist (
  tourist_id INT PRIMARY KEY,
  name VARCHAR(50),
  email VARCHAR(100),
  nationality VARCHAR(50)
);

CREATE TABLE improvement_suggestions (
  suggestion_id INT PRIMARY KEY,
  feedback_id INT,
  suggestion TEXT
);

CREATE TABLE initiative_feedback (
  feedback_id INT,
  initiative_id INT,
  improvement_suggestion_id INT
);
