tables.table_schema:
CREATE TABLE trip_cost (
    trip_id INT PRIMARY KEY,
    location VARCHAR(255),
    cost_per_person DECIMAL(10, 2),
    total_cost DECIMAL(10, 2)
);

CREATE TABLE trips (
  trip_id INT PRIMARY KEY,
  trip_name VARCHAR(255) NOT NULL,
  package_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE revenues (
  revenue_id INT PRIMARY KEY,
  trip_id INT NOT NULL,
  revenue_amount DECIMAL(10, 2) NOT NULL,
  FOREIGN KEY (trip_id) REFERENCES trips(trip_id)
);

CREATE TABLE trip_costs (
  trip_id INT PRIMARY KEY,
  trip_name VARCHAR(255),
  trip_date DATE,
  cost DECIMAL(10, 2)
);

CREATE TABLE revenue_analysis (
  revenue_id INT PRIMARY KEY,
  trip_id INT,
  revenue_amount DECIMAL(10, 2),
  revenue_date DATE
);

CREATE TABLE profitability_analysis (
  analysis_id INT PRIMARY KEY,
  trip_id INT,
  profitability DECIMAL(10, 2),
  analysis_date DATE
);

CREATE TABLE travel_packages (
  package_id INT PRIMARY KEY,
  package_name VARCHAR(255),
  package_description TEXT,
  package_price DECIMAL(10, 2)
);

CREATE TABLE excursions (
  excursion_id INT PRIMARY KEY,
  excursion_name VARCHAR(255),
  excursion_location VARCHAR(255),
  excursion_duration INT
);
