tables.table_schema:
CREATE TABLE historical_images (
  id INT PRIMARY KEY,
  image_url TEXT,
  tags TEXT[],
  description TEXT
);

CREATE TABLE tags (
  tag_id INT PRIMARY KEY,
  tag_name VARCHAR(50)
);

CREATE TABLE photos (
  photo_id INT PRIMARY KEY,
  photo_name VARCHAR(255) NOT NULL,
  tag_id INT,
  description TEXT
);

CREATE TABLE tags (
  tag_id INT PRIMARY KEY,
  tag_name VARCHAR(50) NOT NULL
);

CREATE TABLE photo_tag_map (
  map_id INT PRIMARY KEY,
  photo_id INT,
  tag_id INT
);

CREATE TABLE HistoricalPhotographs (
    id UUID PRIMARY KEY,
    image_url TEXT NOT NULL,
    description TEXT,
    date_taken DATE,
    tag_ids ARRAY<UUID>
);

CREATE TABLE Tag (
    id UUID PRIMARY KEY,
    tag_name TEXT NOT NULL
);

CREATE TABLE ImageTagMapping (
    image_id UUID,
    tag_id UUID,
    PRIMARY KEY (image_id, tag_id),
    FOREIGN KEY (image_id) REFERENCES HistoricalPhotographs(id) ON DELETE CASCADE,
    FOREIGN KEY (tag_id) REFERENCES Tag(id) ON DELETE CASCADE
);

CREATE TABLE ImageComments (
    id UUID PRIMARY KEY,
    image_id UUID,
    comment TEXT NOT NULL,
    commenter_name TEXT,
    comment_date TIMESTAMP,
    FOREIGN KEY (image_id) REFERENCES HistoricalPhotographs(id) ON DELETE CASCADE
);
