tables.table_schema:
CREATE TABLE historical_battles (
    battle_id UUID PRIMARY KEY,
    battle_name STRING,
    battle_date DATE,
    location STRING
);

CREATE TABLE participants (
    participant_id UUID PRIMARY KEY,
    participant_name STRING,
    battle_id UUID REFERENCES historical_battles(battle_id)
);

CREATE TABLE Outcomes (
	outcome_id INT PRIMARY KEY,
	participant_id INT,
	outcome_description TEXT,
	FOREIGN KEY (participant_id) REFERENCES Participants(participant_id)
);

CREATE TABLE armies (
    army_id UUID PRIMARY KEY,
    army_name VARCHAR(255) NOT NULL,
    total_soldiers INT,
    commander_name VARCHAR(255)
);

CREATE TABLE battles_outcomes (
    outcome_id UUID PRIMARY KEY,
    battle_id UUID,
    outcome_description TEXT
);

CREATE TABLE battles_participants (
    id UUID PRIMARY KEY,
    participant_id UUID,
    battle_id UUID
);
