tables.table_schema:
CREATE TABLE ArchitecturalFirm (
 id INT PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	location VARCHAR(255) NOT NULL
);

CREATE TABLE ConstructionProject (
	project_id INT PRIMARY KEY,
	project_name VARCHAR(255) NOT NULL,
	start_date DATE,
	end_date DATE
);

CREATE TABLE CostEstimation (
	estimation_id INT PRIMARY KEY,
	project_id INT,
	total_cost DECIMAL(10, 2),
	FOREIGN KEY (project_id) REFERENCES ConstructionProject(project_id)
);

CREATE TABLE contractor (
  contractor_id UUID PRIMARY KEY,
  contractor_name VARCHAR(255) NOT NULL,
  contact_number VARCHAR(20) NOT NULL,
  email VARCHAR(255) NOT NULL
);

CREATE TABLE materials (
  material_id UUID PRIMARY KEY,
  material_name VARCHAR(255) NOT NULL,
  unit_price DECIMAL(8, 2) NOT NULL,
  quantity INT NOT NULL
);

CREATE TABLE projects (
   project_id INT PRIMARY KEY,
   project_name VARCHAR(255) NOT NULL,
   estimated_cost DECIMAL(10,2) NOT NULL,
   budget DECIMAL(10,2) NOT NULL
);

CREATE TABLE architects (
   architect_id INT PRIMARY KEY,
   architect_name VARCHAR(255) NOT NULL,
   specialization VARCHAR(100) NOT NULL
);

CREATE TABLE clients (
   client_id INT PRIMARY KEY,
   client_name VARCHAR(255) NOT NULL,
   contact_info VARCHAR(255) NOT NULL
);
