tables.table_schema:
CREATE TABLE energy_efficiency_metrics (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    architect_name VARCHAR(255) NOT NULL,
    energy_rating INT
);

CREATE TABLE Architects (
    architect_id INT,
    architect_name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(15)
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255),
    construction_year INT,
    energy_rating FLOAT
);

CREATE TABLE architects (
    architect_id INT PRIMARY KEY,
    architect_name VARCHAR(255),
    expertise_area VARCHAR(255)
);

CREATE TABLE building_designs(
    building_id UUID PRIMARY KEY,
    architect_name TEXT,
    construction_date DATE,
    energy_rating FLOAT
);

CREATE TABLE building_materials(
    material_id UUID PRIMARY KEY,
    material_name TEXT,
    material_type TEXT,
    material_cost DECIMAL(10, 2)
);

CREATE TABLE sustainability_metrics(
    metric_id UUID PRIMARY KEY,
    metric_name TEXT,
    metric_value FLOAT
);

CREATE TABLE architect_profiles(
    architect_id UUID PRIMARY KEY,
    architect_name TEXT,
    company_name TEXT,
    years_of_experience INTEGER
);
