tables.table_schema:
CREATE TABLE population_data (
    city_name STRING,
    population INT,
    land_area FLOAT
);

CREATE TABLE CityPopulation (
    city_name VARCHAR(255),
    population INT,
    land_area DECIMAL(10,2)
);

CREATE TABLE ZoningRegulations (
    city_name VARCHAR(255),
    zoning_code VARCHAR(10),
    zoning_category VARCHAR(50)
);

CREATE TABLE Population_Data (
    city_name STRING,
    population_count INT,
    median_income FLOAT
);

CREATE TABLE Zoning_Regulations (
    zoning_category STRING,
    area_size FLOAT,
    max_building_height INT
);

CREATE TABLE City_Demographics (
    city_name STRING,
    avg_age INT,
    avg_household_income FLOAT
);

CREATE TABLE city_population (
    city_name VARCHAR(255),
    population INTEGER
);

CREATE TABLE zoning_regulations (
    zone_type VARCHAR(255),
    area_size INTEGER,
    building_height INTEGER
);

CREATE TABLE population_density (
    city_name VARCHAR(255),
    density INTEGER
);

CREATE TABLE zoning_permit (
    permit_number VARCHAR(255),
    issue_date DATE
);

CREATE TABLE zoning_regulations (
    zone_id INT PRIMARY KEY,
    zone_name VARCHAR(255),
    allowed_population_density INT,
    land_use VARCHAR(255)
);

CREATE TABLE city_geography (
    city_id INT,
    latitude DECIMAL(10,8),
    longitude DECIMAL(11,8),
    elevation INT
);
