tables.table_schema:
CREATE TABLE crime_location (
    location_id INT PRIMARY KEY,
    location_name TEXT,
    city TEXT,
    state TEXT
);

CREATE TABLE crime_type (
    crime_type_id INT PRIMARY KEY,
    crime_category TEXT,
    crime_description TEXT
);

CREATE TABLE CrimeTypes (
  crime_type_id INT PRIMARY KEY,
  crime_category VARCHAR(50),
  description TEXT
);

CREATE TABLE CrimeLocations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(100),
  city VARCHAR(50),
  state VARCHAR(50)
);
