tables.table_schema:
CREATE TABLE traffic_data (
    id INT PRIMARY KEY,
    vehicle_type VARCHAR(255),
    speed INT,
    time DATETIME
);

CREATE TABLE road_infrastructure (
    road_id INT PRIMARY KEY,
    road_name VARCHAR(255),
    road_type VARCHAR(255),
    traffic_volume INT
);

CREATE TABLE RoadInfrastructure (
  road_id INT PRIMARY KEY,
  road_name VARCHAR,
  road_length INT,
  surface_type VARCHAR
);

CREATE TABLE TrafficFlowData (
    flow_id INT PRIMARY KEY,
    road_id INT,
    flow_rate INT,
    traffic_date DATE
);

CREATE TABLE RoadInfo (
    road_id INT PRIMARY KEY,
    road_name VARCHAR(50),
    road_type VARCHAR(20),
    construction_year INT
);

CREATE TABLE TrafficSignals (
    signal_id INT PRIMARY KEY,
    road_id INT,
    signal_type VARCHAR(20),
    signal_status VARCHAR(20)
);

CREATE TABLE RoadAccidents (
    accident_id INT PRIMARY KEY,
    road_id INT,
    accident_date DATE,
    accident_type VARCHAR(50)
);

CREATE TABLE RoadMaintenance (
    maintenance_id INT PRIMARY KEY,
    road_id INT,
    maintenance_type VARCHAR(50),
    maintenance_date DATE
);
