tables.table_schema:
CREATE TABLE property (
  id UUID PRIMARY KEY,
  address TEXT,
  price MONEY,
  sold BOOLEAN
);

CREATE TABLE Property (
  property_id INT PRIMARY KEY,
  address VARCHAR(255),
  price DECIMAL(10, 2),
  sold BOOLEAN DEFAULT FALSE
);

CREATE TABLE Sale (
  sale_id INT PRIMARY KEY,
  property_id INT REFERENCES Property(property_id),
  sale_date DATE
);

CREATE TABLE Buyer (
  buyer_id INT PRIMARY KEY,
  name VARCHAR(255),
  email VARCHAR(255)
);

CREATE TABLE Payment (
  payment_id INT PRIMARY KEY,
  sale_id INT REFERENCES Sale(sale_id),
  amount DECIMAL(10, 2),
  payment_date DATE
);

CREATE TABLE Properties (
    property_id UUID PRIMARY KEY,
    property_name VARCHAR(255) NOT NULL,
    property_type VARCHAR(50),
    property_price DECIMAL(10, 2) NOT NULL,
    property_status VARCHAR(20) DEFAULT 'available'
);
CREATE TABLE Sales (
    sale_id UUID PRIMARY KEY,
    property_id UUID REFERENCES Properties(property_id),
    sale_date DATE NOT NULL,
    sale_price DECIMAL(10, 2) NOT NULL
);
CREATE TABLE Customers (
    customer_id UUID PRIMARY KEY,
    customer_name VARCHAR(255) NOT NULL,
    customer_email VARCHAR(255) UNIQUE,
    customer_phone VARCHAR(15)
);
CREATE TABLE Agents (
    agent_id UUID PRIMARY KEY,
    agent_name VARCHAR(255) NOT NULL,
    agent_email VARCHAR(255) UNIQUE,
    agent_phone VARCHAR(15)
);
CREATE TABLE PropertyTransactions (
    transaction_id UUID PRIMARY KEY,
    property_id UUID REFERENCES Properties(property_id),
    transaction_date DATE NOT NULL,
    transaction_type VARCHAR(20) NOT NULL,
    transaction_status VARCHAR(20) DEFAULT 'pending'
);
