tables.table_schema:
CREATE TABLE topographic_survey_data (
  id SERIAL PRIMARY KEY,
  point_name VARCHAR(50) NOT NULL,
  elevation DECIMAL(10,2) NOT NULL
);

CREATE TABLE surveyors (
  id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  license_number VARCHAR(20) NOT NULL
);

CREATE TABLE TopographicSurveys (
    survey_id SERIAL PRIMARY KEY,
    survey_name VARCHAR(50),
    survey_date DATE
);

CREATE TABLE SurveyPoints (
    point_id SERIAL PRIMARY KEY,
    survey_id INT REFERENCES TopographicSurveys(survey_id),
    point_name VARCHAR(50),
    point_elevation FLOAT
);

CREATE TABLE SurveyMeasurements (
    measurement_id SERIAL PRIMARY KEY,
    point_id INT REFERENCES SurveyPoints(point_id),
    measurement_type VARCHAR(50),
    measurement_value FLOAT
);

CREATE TABLE AreasOfInterest (
   id SERIAL PRIMARY KEY,
   name VARCHAR(100),
   description TEXT,
   boundary GEOMETRY
);

CREATE TABLE SurveyReports (
  report_id UUID PRIMARY KEY,
  survey_id UUID REFERENCES SurveyData(id) NOT NULL,
  report_date DATE NOT NULL,
  findings TEXT
);

CREATE TABLE Equipment (
  equipment_id UUID PRIMARY KEY,
  equipment_name VARCHAR NOT NULL,
  model VARCHAR NOT NULL,
  manufacturer VARCHAR
);

CREATE TABLE SurveyPhotos (
  photo_id UUID PRIMARY KEY,
  survey_id UUID REFERENCES SurveyData(id) NOT NULL,
  photo_path VARCHAR NOT NULL,
  photo_description TEXT
);
