tables.table_schema:
CREATE TABLE subcontractors (
    subcontractor_id UUID PRIMARY KEY,
    name STRING,
    contact_info STRING,
    address STRING
);

CREATE TABLE subcontractors (
    subcontractor_id INT PRIMARY KEY,
    subcontractor_name VARCHAR(255),
    subcontractor_address VARCHAR(255),
    subcontractor_phone VARCHAR(15)
);

CREATE TABLE subcontractors (
    id UUID PRIMARY KEY,
    name STRING NOT NULL,
    address STRING,
    contact_number STRING
);

CREATE TABLE subcontractors (
    subcontractor_id INT PRIMARY KEY,
    name VARCHAR(255),
    address VARCHAR(255),
    contact_number VARCHAR(20)
);

CREATE TABLE services (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(100),
    description TEXT
);

CREATE TABLE subcontractor_services (
    subcontractor_id INT,
    service_id INT,
    FOREIGN KEY (subcontractor_id) REFERENCES subcontractors(subcontractor_id),
    FOREIGN KEY (service_id) REFERENCES services(service_id)
);
