CREATE TABLE construction_projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE project_expense (project_id UUID REFERENCES project(project_id),
expense_id UUID REFERENCES expense(expense_id),
FOREIGN KEY (project_id, expense_id) REFERENCES project(project_id, expense_id));
