tables.table_schema:
CREATE TABLE BuildingMaterialsInventory (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255),
    material_quantity INT,
    material_location VARCHAR(255)
);

CREATE TABLE building_materials (
  material_id INT PRIMARY KEY,
  material_name STRING,
  quantity INT
);

CREATE TABLE inventory_log (
  log_id INT PRIMARY KEY,
  material_id INT REFERENCES building_materials(material_id),
  timestamp TIMESTAMP,
  action STRING
);

CREATE TABLE BuildingMaterials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255),
    quantity INT,
    location VARCHAR(255)
);

CREATE TABLE InventoryLogs (
    log_id INT PRIMARY KEY,
    material_id INT,
    quantity_change INT,
    timestamp TIMESTAMP
);

CREATE TABLE MaterialSuppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    material_id INT,
    contact_info VARCHAR(255)
);

CREATE TABLE building_materials (
    material_id UUID PRIMARY KEY,
    material_name STRING,
    quantity INT,
    location STRING
);

CREATE TABLE material_suppliers (
    supplier_id UUID PRIMARY KEY,
    supplier_name STRING,
    contact_info STRING
);
