tables.table_schema:
CREATE TABLE building_material_prices (
   material_id INT PRIMARY KEY,
   material_name VARCHAR(255),
   price DECIMAL(10,2),
   date_recorded DATE
);

CREATE TABLE BuildingMaterials (
  material_id INT PRIMARY KEY,
  material_name TEXT,
  price DECIMAL,
  supplier_id INT
);

CREATE TABLE building_materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255),
    price DECIMAL(10, 2),
    date TIMESTAMP
);

CREATE TABLE material_suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    contact_number VARCHAR(20)
);

CREATE TABLE building_materials (
    id INT PRIMARY KEY,
    material_name TEXT,
    price DECIMAL,
    date_added DATE
);

CREATE TABLE suppliers (
    id INT PRIMARY KEY,
    supplier_name TEXT,
    location TEXT
);

CREATE TABLE material_availability (
    material_id INT REFERENCES building_materials(id),
    supplier_id INT REFERENCES suppliers(id),
    available_quantity INT,
    last_updated TIMESTAMP
);

CREATE TABLE BuildingMaterials (
	material_id INT PRIMARY KEY,
	material_name TEXT,
	price DECIMAL
);

CREATE TABLE MaterialStyles (
	style_id INT PRIMARY KEY,
	style_name TEXT,
	material_id INT,
	FOREIGN KEY (material_id) REFERENCES BuildingMaterials(material_id)
);

CREATE TABLE Suppliers (
	supplier_id INT PRIMARY KEY,
	supplier_name TEXT,
	material_id INT,
	FOREIGN KEY (material_id) REFERENCES BuildingMaterials(material_id)
);
