tables.table_schema:
CREATE TABLE building_materials (
    material_id SERIAL PRIMARY KEY,
    material_name VARCHAR(100) NOT NULL,
    material_type VARCHAR(50) NOT NULL,
    material_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE building_materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255),
    material_category VARCHAR(255),
    material_description TEXT
);

CREATE TABLE technical_data (
    data_id INT PRIMARY KEY,
    material_id INT,
    technical_specifications TEXT
    FOREIGN KEY (material_id) REFERENCES building_materials(material_id)
);

CREATE TABLE BuildingMaterials (
    material_id SERIAL PRIMARY KEY,
    material_name VARCHAR(255) NOT NULL,
    material_type VARCHAR(50) NOT NULL,
    material_description TEXT
);

CREATE TABLE Suppliers (
    supplier_id SERIAL PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    supplier_location VARCHAR(100) NOT NULL
);
