    quantity INT
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(100),
    location VARCHAR(100)
);

CREATE TABLE building_material_usage (
    usage_id INT PRIMARY KEY,
    building_id INT REFERENCES buildings(building_id),
    material_id INT REFERENCES building_materials(material_id),
    quantity_used INT
);

CREATE TABLE building_materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(50) NOT NULL,
    impact_category VARCHAR(50) NOT NULL
);

CREATE TABLE material_properties (
    property_id INT PRIMARY KEY,
    material_id INT NOT NULL,
    property_name VARCHAR(50) NOT NULL,
    property_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE project_materials (
    project_id INT NOT NULL,
    material_id INT NOT NULL,
    quantity DECIMAL(10, 2) NOT NULL,
    PRIMARY KEY (project_id, material_id)
);
