tables.table_schema:
CREATE TABLE virtual_machines (
    vm_id INT PRIMARY KEY,
    vm_name VARCHAR(50) NOT NULL,
    vm_template TEXT NOT NULL
);

CREATE TABLE containers (
    container_id INT PRIMARY KEY,
    container_name VARCHAR(50) NOT NULL,
    container_image VARCHAR(100) NOT NULL
);

CREATE TABLE container_templates (
    template_id UUID PRIMARY KEY,
    template_name VARCHAR(50) NOT NULL,
    image_name VARCHAR(100) NOT NULL,
    port INT
);

CREATE TABLE provision_history (
    provision_id UUID PRIMARY KEY,
    provision_date TIMESTAMP NOT NULL,
    template_id UUID REFERENCES vm_templates(template_id) ON DELETE CASCADE,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE vm_provisioning_history (
    provision_id INT PRIMARY KEY,
    template_id INT NOT NULL,
    provision_date TIMESTAMP NOT NULL,
    provision_status VARCHAR(20) NOT NULL
);

CREATE TABLE container_provisioning_history (
    provision_id INT PRIMARY KEY,
    template_id INT NOT NULL,
    provision_date TIMESTAMP NOT NULL,
    provision_status VARCHAR(20) NOT NULL
);
