tables.table_schema:
CREATE TABLE network_devices (
    device_id UUID PRIMARY KEY,
    device_name STRING,
    device_type STRING,
    location STRING
);

CREATE TABLE trends (
    trend_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    trend_type STRING,
    trend_value FLOAT
);

CREATE TABLE bottlenecks (
    bottleneck_id UUID PRIMARY KEY,
    device_id UUID,
    timestamp TIMESTAMP,
    issue_description STRING
);
