CREATE TABLE documentation (
  id INT PRIMARY KEY,
  title VARCHAR(255),
  content TEXT,
  created_at TIMESTAMP
);

CREATE TABLE runbooks (
  id INT PRIMARY KEY,
  title VARCHAR(255),
  steps TEXT,
  created_at TIMESTAMP
);

CREATE TABLE categories (
  id INT PRIMARY KEY,
  name VARCHAR(50)
);

CREATE TABLE tags (
  id INT PRIMARY KEY,
