tables.table_schema:
CREATE TABLE InfrastructureProjects (
 project_id UUID PRIMARY KEY,
 project_name VARCHAR(255) NOT NULL,
 project_location VARCHAR(255) NOT NULL
);

CREATE TABLE DesignDrawings (
 drawing_id UUID PRIMARY KEY,
 project_id UUID REFERENCES InfrastructureProjects(project_id),
 drawing_name VARCHAR(255) NOT NULL,
 drawing_file BYTEA NOT NULL
);

CREATE TABLE infrastructure_projects (
    project_id INT PRIMARY KEY,
    project_name TEXT,
    project_location TEXT,
    project_start_date DATE
);

CREATE TABLE engineers (
    engineer_id INT PRIMARY KEY,
    engineer_name TEXT,
    engineer_specialization TEXT,
    engineer_experience_years INT
);

CREATE TABLE CivilEngineeringFirm (
firm_id INT,
firm_name VARCHAR(255),
