tables.table_schema:
CREATE TABLE before_after_photos (
    photo_id INT PRIMARY KEY,
    photo_url TEXT,
    renovation_project_name TEXT,
    contractor_id INT
);

CREATE TABLE material_cost_estimates (
    estimate_id INT PRIMARY KEY,
    project_name TEXT,
    material_name TEXT,
    quantity INT,
    cost DECIMAL
);

CREATE TABLE before_after_photos (
  id INT PRIMARY KEY,
  project_id INT,
  before_photo_url VARCHAR,
  after_photo_url VARCHAR
);

CREATE TABLE material_cost_estimates (
  id INT PRIMARY KEY,
  project_id INT,
  material_item VARCHAR,
  estimated_cost DECIMAL
);

CREATE TABLE contractor_information (
  id INT PRIMARY KEY,
  contractor_id INT,
  contractor_name VARCHAR,
  contractor_specialty VARCHAR
);

CREATE TABLE before_and_after_photos (
    id SERIAL PRIMARY KEY,
    renovation_id INT NOT NULL,
    before_photo_url VARCHAR(255),
    after_photo_url VARCHAR(255)
);

CREATE TABLE material_cost_estimates (
    id SERIAL PRIMARY KEY,
    renovation_id INT NOT NULL,
    material_name VARCHAR(255),
    cost_estimate DECIMAL(10, 2)
);

CREATE TABLE contractor_information (
    id SERIAL PRIMARY KEY,
    contractor_id INT NOT NULL,
    contractor_name VARCHAR(255),
    contractor_phone VARCHAR(15)
);

CREATE TABLE renovation_projects (
    id SERIAL PRIMARY KEY,
    project_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE renovation_details (
    id SERIAL PRIMARY KEY,
    project_id INT NOT NULL,
    description TEXT,
    notes TEXT
);
