CREATE TABLE kitchen_layout (
    layout_id SERIAL PRIMARY KEY,
    customer_name VARCHAR(50),
    design_drawings TEXT,
    date_created DATE
);

CREATE TABLE kitchen_layout (
    id INT PRIMARY KEY,
    room_name VARCHAR(50),
    layout_image BYTEA
);

CREATE TABLE customer_reviews (
    review_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    review_text TEXT,
    rating INT
);
