tables.table_schema:
CREATE TABLE building_certification (
    id INT PRIMARY KEY,
    building_name VARCHAR(255),
    certification_status VARCHAR(50),
    certification_date DATE
);

CREATE TABLE green_building_certification (
    building_id INT PRIMARY KEY,
    certification_level VARCHAR(50) NOT NULL,
    certification_date DATE
);

CREATE TABLE building_owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(50) NOT NULL,
    building_id INT,
    FOREIGN KEY (building_id) REFERENCES green_building_certification(building_id)
);
