tables.table_schema:
CREATE TABLE energy_consumption (
    building_id INT,
    date DATE,
    energy_usage DECIMAL
);

CREATE TABLE energy_consumption_data (
    building_id INT,
    date DATE,
    time TIME,
    energy_consumed DECIMAL
);

CREATE TABLE EnergyConsumptionData (
    building_id INT NOT NULL,
    date DATE NOT NULL,
    energy_consumed DECIMAL NOT NULL,
    PRIMARY KEY (building_id, date)
);

CREATE TABLE BuildingInformation (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    area_sqft DECIMAL NOT NULL
);

CREATE TABLE EnergyConsumptionData (
   building_id INT,
   timestamp TIMESTAMP,
   electricity_consumed DECIMAL,
   water_consumed DECIMAL
);

CREATE TABLE BuildingInformation (
   building_id INT,
   building_name VARCHAR,
   location VARCHAR,
   building_type VARCHAR
);

CREATE TABLE OptimizationResults (
   building_id INT,
   optimization_timestamp TIMESTAMP,
   energy_saved DECIMAL,
   water_saved DECIMAL
);

CREATE TABLE building_info (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE energy_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL
);

CREATE TABLE weather_data (
    date DATE PRIMARY KEY,
    temperature DECIMAL NOT NULL,
    humidity DECIMAL NOT NULL
);
