tables.table_schema:
CREATE TABLE water_usage_data (
    id INT PRIMARY KEY,
    building_name VARCHAR(255),
    water_consumption DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE building_info (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255),
    location VARCHAR(255),
    construction_date DATE
);

CREATE TABLE WaterUsageData (
    id INT PRIMARY KEY,
    building_id INT,
    consumption FLOAT,
    date DATE
);

CREATE TABLE Building (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    address VARCHAR(100),
    build_year INT
);

CREATE TABLE GreenBuildingInitiatives (
    id INT PRIMARY KEY,
    building_id INT,
    certification VARCHAR(50)
);
