tables.table_schema:
CREATE TABLE GreenBuildingProjects (
  project_id UUID PRIMARY KEY,
  project_name VARCHAR(100),
  project_description TEXT,
  project_location VARCHAR(255)
);

CREATE TABLE ProjectMembers (
  member_id UUID PRIMARY KEY,
  member_name VARCHAR(100),
  member_role VARCHAR(50),
  project_id UUID REFERENCES GreenBuildingProjects(project_id)
);

CREATE TABLE GreenBuildingProjects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    project_description TEXT,
    project_leader VARCHAR(100)
);

CREATE TABLE TeamMembers (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(100),
    member_role VARCHAR(50)
);

CREATE TABLE Green_Building_Projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(50),
    project_description TEXT,
    project_location VARCHAR(100)
);

CREATE TABLE Project_Team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    team_role VARCHAR(50)
);

CREATE TABLE Team_Members (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50),
    team_id INT,
    FOREIGN KEY (team_id) REFERENCES Project_Team(team_id)
);
