tables.table_schema:
CREATE TABLE waste_collection (
    collection_id INT PRIMARY KEY,
    collection_date DATE,
    collection_weight DECIMAL
);

CREATE TABLE waste_disposal (
    disposal_id INT PRIMARY KEY,
    disposal_date DATE,
    disposal_method VARCHAR(50),
    disposal_location VARCHAR(100)
);

CREATE TABLE waste_bins (
    id INT PRIMARY KEY,
    bin_location VARCHAR(255),
    waste_type VARCHAR(100),
    capacity INT
);

CREATE TABLE recycling_centers (
    center_id INT PRIMARY KEY,
    center_location VARCHAR(255),
    accepted_materials TEXT,
    recycling_capacity INT
);
