tables.table_schema:
CREATE TABLE survey_questionnaires (
    questionnaire_id INT PRIMARY KEY,
    question_text TEXT,
    created_date TIMESTAMP
);

CREATE TABLE questionnaires (
    questionnaire_id INT PRIMARY KEY,
    questionnaire_text TEXT
);

CREATE TABLE answers (
    answer_id INT PRIMARY KEY,
    questionnaire_id INT,
    answer_text TEXT,
    FOREIGN KEY (questionnaire_id) REFERENCES questionnaires(questionnaire_id)
);

CREATE TABLE SurveyQuestions(
    id INT PRIMARY KEY,
    question TEXT
);

CREATE TABLE SurveyAnswers(
    id INT PRIMARY KEY,
    question_id INT,
    answer TEXT
);

CREATE TABLE SurveyParticipants(
    id INT PRIMARY KEY,
    participant_name TEXT,
    email TEXT
);

CREATE TABLE SurveyQuestionnaires (
    survey_id SERIAL PRIMARY KEY,
    title TEXT NOT NULL,
    question TEXT
);

CREATE TABLE SurveyAnswers (
    answer_id SERIAL PRIMARY KEY,
    survey_id INT REFERENCES SurveyQuestionnaires(survey_id),
    respondent_name TEXT NOT NULL,
    answer_text TEXT NOT NULL
);

CREATE TABLE Respondents (
    respondent_id SERIAL PRIMARY KEY,
    respondent_name TEXT NOT NULL,
    email TEXT
);

CREATE TABLE SurveyUsers (
    user_id SERIAL PRIMARY KEY,
    username TEXT NOT NULL,
    password TEXT NOT NULL,
    email TEXT
);

CREATE TABLE SurveyLogs (
    log_id SERIAL PRIMARY KEY,
    survey_id INT REFERENCES SurveyQuestionnaires(survey_id),
    log_time TIMESTAMP NOT NULL,
    action_taken TEXT
);
