tables.table_schema:
CREATE TABLE IF NOT EXISTS survey_data (
    survey_id INT PRIMARY KEY,
    participant_name VARCHAR(50),
    survey_questions JSONB,
    submission_date TIMESTAMP
);

CREATE TABLE survey_data (
    survey_id INT PRIMARY KEY,
    participant_id INT,
    survey_question TEXT,
    survey_answer TEXT
);

CREATE TABLE participant_data (
    participant_id INT PRIMARY KEY,
    participant_name TEXT,
    participant_email TEXT,
    participant_role TEXT
);

CREATE TABLE survey_data (
  survey_id INT PRIMARY KEY,
  survey_name VARCHAR(255) NOT NULL,
  survey_date DATE,
  respondent_name VARCHAR(255) NOT NULL,
  respondent_email VARCHAR(255) UNIQUE
);

CREATE TABLE question_data (
  question_id INT PRIMARY KEY,
  question_text TEXT NOT NULL,
  survey_id INT REFERENCES survey_data(survey_id),
  question_type VARCHAR(50)
);

CREATE TABLE answer_data (
  answer_id INT PRIMARY KEY,
  answer_text TEXT NOT NULL,
  question_id INT REFERENCES question_data(question_id),
  respondent_id INT
);

CREATE TABLE surveys (
  id INT PRIMARY KEY,
  survey_name TEXT NOT NULL,
  survey_data JSONB,
  created_at TIMESTAMP
);

CREATE TABLE respondents (
  id INT PRIMARY KEY,
  respondent_name TEXT NOT NULL,
  respondent_email TEXT UNIQUE,
  created_at TIMESTAMP
);

CREATE TABLE questions (
  id INT PRIMARY KEY,
  question_text TEXT NOT NULL,
  question_type TEXT,
  created_at TIMESTAMP
);

CREATE TABLE surveys (
	id INT PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	email VARCHAR(255) NOT NULL,
	survey_data JSONB
);

CREATE TABLE responses (
	response_id INT PRIMARY KEY,
	survey_id INT REFERENCES surveys(id),
	email VARCHAR(255) NOT NULL,
	response_data JSONB
);

CREATE TABLE survey_questions (
	question_id INT PRIMARY KEY,
	survey_id INT REFERENCES surveys(id),
	question_text TEXT NOT NULL,
	question_type VARCHAR(50) NOT NULL
);

CREATE TABLE users (
	user_id INT PRIMARY KEY,
	username VARCHAR(100) UNIQUE NOT NULL,
	password VARCHAR(255) NOT NULL,
	role VARCHAR(50) NOT NULL
);

CREATE TABLE user_roles (
	role_id INT PRIMARY KEY,
	role_name VARCHAR(50) UNIQUE NOT NULL
);
