tables.table_schema:
CREATE TABLE social_analysis (
	id UUID PRIMARY KEY,
	developer_name VARCHAR(50) NOT NULL,
	issue VARCHAR(100) NOT NULL,
	analysis_date DATE
);

CREATE TABLE SocialIssues (
    id INT PRIMARY KEY,
    issue_name TEXT,
    issue_description TEXT,
    issue_category TEXT
);

CREATE TABLE social_trends (
    issue_id INT PRIMARY KEY,
    issue_name TEXT NOT NULL,
    issue_category TEXT,
    trend_score FLOAT
);

CREATE TABLE strategic_planning (
    plan_id INT PRIMARY KEY,
    issue_id INT,
    strategy TEXT
);

CREATE TABLE SocialIssues (
    id INT PRIMARY KEY,
    issue_name VARCHAR(100) NOT NULL,
    description TEXT,
    affected_population VARCHAR(100) NOT NULL
);

CREATE TABLE Interventions (
    id INT PRIMARY KEY,
    intervention_name VARCHAR(100) NOT NULL,
    intervention_type VARCHAR(50) NOT NULL,
    issue_id INT NOT NULL
);

CREATE TABLE Trends (
    id INT PRIMARY KEY,
    trend_name VARCHAR(100) NOT NULL,
    trend_description TEXT,
    analysis_id INT NOT NULL
);
