CREATE TABLE Events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(255) NOT NULL,
    event_date DATE,
    event_location VARCHAR(255) NOT NULL
);

CREATE TABLE Volunteers (
    volunteer_id INT PRIMARY KEY,
    volunteer_name VARCHAR(255) NOT NULL,
    volunteer_email VARCHAR(255) UNIQUE
);

CREATE TABLE ShiftCategories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE ShiftAssignments (
    assignment_id INT PRIMARY KEY,
    shift_id INT REFERENCES Shifts(shift_id),
    volunteer_id INT REFERENCES Volunteers(volunteer_id),
    category_id INT REFERENCES ShiftCategories(category_id)
);
