tables.table_schema:
CREATE TABLE certifications (
    certification_id INT PRIMARY KEY,
    certification_name VARCHAR(50),
    expiration_date DATE
);

CREATE TABLE volunteer_training_certifications (
  certification_id INT PRIMARY KEY,
  volunteer_id INT,
  certification_name TEXT,
  expiration_date DATE
);

CREATE TABLE volunteers (
  volunteer_id INT PRIMARY KEY,
  first_name TEXT,
  last_name TEXT,
  email TEXT
);

CREATE TABLE certification_categories (
  category_id INT PRIMARY KEY,
  category_name TEXT
);

CREATE TABLE training_certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(255) NOT NULL,
  expiration_date DATE
);

CREATE TABLE volunteers (
  volunteer_id INT PRIMARY KEY,
  volunteer_name VARCHAR(255) NOT NULL,
  contact_number VARCHAR(20),
  email_address VARCHAR(255)
);

CREATE TABLE training_instructors (
  instructor_id INT PRIMARY KEY,
  instructor_name VARCHAR(255) NOT NULL,
  contact_number VARCHAR(20),
  email_address VARCHAR(255)
);
