tables.table_schema:
CREATE TABLE volunteers (
    id UUID PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    phone VARCHAR(15),
    skills TEXT
);

CREATE TABLE opportunities (
    id UUID PRIMARY KEY,
    title VARCHAR(100),
    description TEXT,
    location VARCHAR(100),
    required_skills TEXT
);

CREATE TABLE volunteer_opportunities (
    volunteer_id UUID,
    opportunity_id UUID,
    status VARCHAR(20),
    PRIMARY KEY (volunteer_id, opportunity_id)
);

CREATE TABLE skills (
    id SERIAL PRIMARY KEY,
    skill_name VARCHAR(50)
);
