tables.table_schema:
CREATE TABLE Volunteer_Preferences (
    volunteer_id UUID PRIMARY KEY,
    preference TEXT
);

CREATE TABLE Volunteer_Availability (
    volunteer_id UUID PRIMARY KEY,
    day_of_week TEXT,
    available_times TEXT
);

CREATE TABLE VolunteerPreferences (
    volunteer_id INT,
    preference VARCHAR(50),
    availability VARCHAR(50)
);

CREATE TABLE VolunteerAvailability (
    volunteer_id INT,
    day_of_week VARCHAR(10),
    available_start_time TIME,
    available_end_time TIME
);

CREATE TABLE VolunteerContactInfo (
    volunteer_id INT,
    contact_name VARCHAR(50),
    contact_email VARCHAR(50),
    contact_phone VARCHAR(20)
);

CREATE TABLE VolunteerSkills (
    volunteer_id INT,
    skill_type VARCHAR(50),
    proficiency_level VARCHAR(20)
);

CREATE TABLE Skills (
  skill_id SERIAL PRIMARY KEY,
  skill_name VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE VolunteerSkills (
  volunteer_id INT REFERENCES Volunteers (volunteer_id),
  skill_id INT REFERENCES Skills (skill_id),
  PRIMARY KEY (volunteer_id, skill_id)
);

CREATE TABLE Assignments (
  assignment_id SERIAL PRIMARY KEY,
  volunteer_id INT REFERENCES Volunteers (volunteer_id),
  assignment_name VARCHAR(100) NOT NULL,
  status VARCHAR(20) DEFAULT 'Pending'
);
