tables.table_schema:
CREATE TABLE ReliefEfforts (
  relief_id INT PRIMARY KEY,
  location VARCHAR(255),
  date DATE,
  total_expenses DECIMAL
);

CREATE TABLE disaster_areas (
 area_id INT PRIMARY KEY,
 area_name VARCHAR(50) NOT NULL,
 relief_efforts INT
);

CREATE TABLE relief_workers (
 worker_id INT PRIMARY KEY,
 worker_name VARCHAR(50) NOT NULL,
 area_id INT,
 FOREIGN KEY (area_id) REFERENCES disaster_areas(area_id)
);

CREATE TABLE relief_supplies (
 supply_id INT PRIMARY KEY,
 supply_name VARCHAR(50) NOT NULL,
 quantity INT
);

CREATE TABLE donors (
  donor_id INT,
  organization_id INT,
  donation_amount DECIMAL,
  donation_date DATE
);

CREATE TABLE AreaImpact (
  area_id INT NOT NULL,
  relief_efforts IMPACT_TYPE NOT NULL,
  effectiveness_rating DECIMAL(5,2) NOT NULL
);

CREATE TABLE Donations (
  donation_id INT NOT NULL,
  donor_name VARCHAR(50) NOT NULL,
  amount DECIMAL(10,2) NOT NULL,
  donation_date DATE NOT NULL
);
