tables.table_schema:
CREATE TABLE environmental_impact (
    impact_id UUID PRIMARY KEY,
    initiative_id UUID REFERENCES sustainability_initiative(initiative_id) NOT NULL,
    impact_type VARCHAR(50) NOT NULL,
    impact_score INT NOT NULL
);

CREATE TABLE reduction_strategies (
    strategy_id UUID PRIMARY KEY,
    initiative_id UUID REFERENCES sustainability_initiative(initiative_id) NOT NULL,
    strategy_name VARCHAR(100) NOT NULL,
    implementation_date DATE NOT NULL
);

CREATE TABLE Strategy (
 id INT NOT NULL,
 name VARCHAR(50) NOT NULL,
 description TEXT,
 PRIMARY KEY (id)
);
