tables.table_schema:
CREATE TABLE government_spending (
  id INT PRIMARY KEY,
  department TEXT,
  amount DECIMAL
);

CREATE TABLE budget_planning (
  id INT PRIMARY KEY,
  year INT,
  total_budget DECIMAL
);

CREATE TABLE government_spending_data (
    id INT PRIMARY KEY,
    department_name VARCHAR(255),
    expenditure_amount DECIMAL(10,2),
    expenditure_date DATE
);

CREATE TABLE budget_planning (
    id INT PRIMARY KEY,
    department_name VARCHAR(255),
    budget_amount DECIMAL(10,2),
    budget_year INT
);

CREATE TABLE analysis_logs (
    id INT PRIMARY KEY,
    user_id INT,
    analysis_date DATETIME,
    analysis_result TEXT
);

CREATE TABLE government_departments (
    department_id INT PRIMARY KEY,
    department_name TEXT
);

CREATE TABLE government_agencies (
    agency_id INT PRIMARY KEY,
    agency_name TEXT,
    department_id INT,
    FOREIGN KEY (department_id) REFERENCES government_departments(department_id)
);

CREATE TABLE spending_categories (
    category_id INT PRIMARY KEY,
    category_name TEXT
);
