tables.table_schema:
CREATE TABLE policy_decisions (
    decision_id INT PRIMARY KEY,
    decision_title VARCHAR(255),
    decision_date DATE,
    department VARCHAR(255)
);

CREATE TABLE policy_decisions (
    decision_id UUID PRIMARY KEY,
    decision_date DATE NOT NULL,
    decision_maker VARCHAR(100) NOT NULL,
    decision_description TEXT
);

CREATE TABLE crime_data (
   crime_id INT PRIMARY KEY,
   crime_type VARCHAR(50),
   crime_location VARCHAR(100),
   crime_date DATE
);

CREATE TABLE law_enforcement (
   agency_id INT PRIMARY KEY,
   agency_name VARCHAR(100),
   agency_location VARCHAR(100),
   agency_contact VARCHAR(20)
);

CREATE TABLE LawEnforcement (
  agency_id UUID PRIMARY KEY,
  agency_name VARCHAR(100) NOT NULL,
  jurisdiction VARCHAR(100) NOT NULL
);
