tables.table_schema:
CREATE TABLE Transportation_Data (
  id INT PRIMARY KEY,
  vehicle_type VARCHAR,
  route_id INT,
  passenger_count INT
);

CREATE TABLE Infrastructure_Planning (
  id INT PRIMARY KEY,
  project_name VARCHAR,
  estimated_cost DECIMAL,
  completion_date DATE
);

CREATE TABLE transportation_stations (
    station_id INT PRIMARY KEY,
    station_name VARCHAR(255),
    location POINT,
    operational_status BOOLEAN
);
