tables.table_schema:
CREATE TABLE ProgramOutcomes (
  id INT PRIMARY KEY,
  program_id INT,
  outcome_description TEXT,
  achievement_rate FLOAT
);

CREATE TABLE PolicyEvaluation (
  id INT PRIMARY KEY,
  policy_id INT,
  evaluation_criteria TEXT,
  effectiveness_score FLOAT
);

CREATE TABLE ResearchAssociations (
  id INT PRIMARY KEY,
  researcher_id INT,
  program_id INT,
  policy_id INT
);

CREATE TABLE researchers (
  researcher_id UUID PRIMARY KEY,
  name TEXT,
  specialization TEXT
);

CREATE TABLE program_enrollments (
  enrollment_id UUID PRIMARY KEY,
  program_id UUID REFERENCES education_programs(program_id),
  enrollment_date DATE,
  student_id UUID
);

CREATE TABLE evaluation_reports (
  report_id UUID PRIMARY KEY,
  project_id UUID REFERENCES research_projects(project_id),
  evaluation_date DATE,
  findings TEXT
);
