tables.table_schema:
CREATE TABLE public.policies (
  policy_id INT PRIMARY KEY,
  policy_name VARCHAR(255) NOT NULL,
  policy_year INT,
  policy_category VARCHAR(50)
);

CREATE TABLE public.policy_changes (
  change_id INT PRIMARY KEY,
  policy_id INT,
  change_date DATE,
  change_description TEXT
);

CREATE TABLE PublicPolicyLegislation (
    legislation_id SERIAL PRIMARY KEY,
    legislation_title TEXT NOT NULL,
    legislative_body TEXT NOT NULL,
    date_enacted DATE NOT NULL
);

CREATE TABLE LegislativeChanges (
    change_id SERIAL PRIMARY KEY,
    legislation_id INT REFERENCES PublicPolicyLegislation(legislation_id),
    change_type TEXT NOT NULL,
    change_description TEXT
);

CREATE TABLE PolicyAnalysts (
    analyst_id SERIAL PRIMARY KEY,
    analyst_name TEXT NOT NULL,
    analyst_field TEXT
);

CREATE TABLE legislators (
    legislator_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    party VARCHAR(50),
    state VARCHAR(2) NOT NULL
);

CREATE TABLE bills (
    bill_id INT PRIMARY KEY,
    title VARCHAR(100) NOT NULL,
    sponsor_id INT,
    introduced_date DATE
);

CREATE TABLE votes (
    vote_id INT PRIMARY KEY,
    bill_id INT,
    legislator_id INT,
    vote_type VARCHAR(10) NOT NULL
);

CREATE TABLE committees (
    committee_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    chair_id INT
);

CREATE TABLE sessions (
    session_id INT PRIMARY KEY,
    start_date DATE,
    end_date DATE
);
