tables.table_schema:
CREATE TABLE Foundations (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    mission_statement TEXT
);

CREATE TABLE Recipients (
   recipient_id INT PRIMARY KEY,
   recipient_name VARCHAR(255),
   city VARCHAR(100),
   country VARCHAR(100)
);

CREATE TABLE Donors (
   donor_id INT PRIMARY KEY,
   donor_name VARCHAR(255),
   city VARCHAR(100),
   country VARCHAR(100)
);
